mapper.select:
select u.id,u.username,u.name,u.nickname,r.name as roleName,u.age,u.lv,u.email,u.telephone,u.sex,u.company,u.address,u.position,u.idcard,u.remark
from
(
select * from user where isdel=0

	and username like '%${userName}%'



	and parent_user = #{parentUser}



	limit #{start,jdbcType=INTEGER},#{listRows,jdbcType=INTEGER}

) as u inner join
(
select * from role where 1=1

	and name like '%${roleName}%'

) as r on u.role_id = r.id

select count(*) from ( select * from user where isdel=0
and username like '%${userName}%'
and parent_user = #{parentUser}
) as u inner join ( select * from role where 1=1
and name like '%${roleName}%'
) as r on u.role_id = r.id
