mapper.select:
select u.id,u.username,u.name,u.nickname,r.name as roleName,u.age,u.lv,u.email,u.telephone,u.sex,u.company,u.address,u.position,u.idcard,u.role_id,u.remark from ( select * from user where isdel=0
and username like '%${userName}%'
and (parent_user = #{parentUser} or id = #{userId})
and role_id = #{roleId}
limit #{start,jdbcType=INTEGER},#{listRows,jdbcType=INTEGER}
) as u inner join ( select * from role where 1=1
and name like '%${roleName}%'
) as r on u.role_id = r.id

select count(*)
from
(
select * from user where isdel=0 and disused=1

	and username like '%${userName}%'



	and parent_user = #{parentUser}



	and role_id = #{roleId}


) as u inner join
(
select * from role where 1=1


	and name like '%${roleName}%'

) as r on u.role_id = r.id

select u.id,u.username,u.name,u.nickname,r.name as
roleName,u.age,u.lv,u.email,u.telephone,u.sex,u.company,u.address,u.position,u.idcard,u.remark
from
(
select * from user where isdel=0 and disused=1

	and username like '%${userName}%'



	and parent_user = #{parentUser}



	and role_id = #{roleId}

) as u inner join
(
select * from role where 1=1

	and name like '%${roleName}%'

) as r on u.role_id = r.id

	limit #{start,jdbcType=INTEGER},#{listRows,jdbcType=INTEGER}

